INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (1, 'Day 1: Introduction to Trading and Types of Financial Markets', false, 
'[{"number": 1, "text": "What is the primary difference between trading and investing?", "options": [{"letter": "a", "text": "Trading involves higher amounts of money"}, {"letter": "b", "text": "Trading focuses on short-term price movements for profit"}, {"letter": "c", "text": "Investing is only for professionals"}, {"letter": "d", "text": "There is no difference"}]}, {"number": 2, "text": "Which market operates 24/7 without closing?", "options": [{"letter": "a", "text": "Stock Market"}, {"letter": "b", "text": "Forex Market"}, {"letter": "c", "text": "Cryptocurrency Market"}, {"letter": "d", "text": "Commodities Market"}]}, {"number": 3, "text": "What does ETF stand for?", "options": [{"letter": "a", "text": "Electronic Trading Fund"}, {"letter": "b", "text": "Exchange-Traded Fund"}, {"letter": "c", "text": "European Trading Foundation"}, {"letter": "d", "text": "Equity Transfer Fund"}]}, {"number": 4, "text": "Which market is considered the largest by daily trading volume?", "options": [{"letter": "a", "text": "Stock Market"}, {"letter": "b", "text": "Forex Market"}, {"letter": "c", "text": "Cryptocurrency Market"}, {"letter": "d", "text": "Commodities Market"}]}, {"number": 5, "text": "What is the main risk associated with CFD trading?", "options": [{"letter": "a", "text": "Limited trading hours"}, {"letter": "b", "text": "High minimum capital requirements"}, {"letter": "c", "text": "Amplified losses due to leverage"}, {"letter": "d", "text": "Inability to trade different markets"}]}]'::jsonb,
'["b", "c", "b", "b", "c"]'::jsonb,
'Test your understanding of trading basics and market types');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (2, 'Day 2: Setting Up a Virtual/Demo Account', false, 
'[{"number": 1, "text": "What is the main purpose of a demo trading account?", "options": [{"letter": "a", "text": "To make real profits without risk"}, {"letter": "b", "text": "To practice trading with virtual money and learn platform mechanics"}, {"letter": "c", "text": "To compete with other traders"}, {"letter": "d", "text": "To access premium market data"}]}, {"number": 2, "text": "Which statement about demo trading is most accurate?", "options": [{"letter": "a", "text": "Demo success guarantees live trading success"}, {"letter": "b", "text": "Demo trading is completely useless for learning"}, {"letter": "c", "text": "Demo trading builds mechanics and process skills that transfer to live trading"}, {"letter": "d", "text": "Demo accounts always show delayed prices"}]}, {"number": 3, "text": "What is eToro's standout feature mentioned in the lesson?", "options": [{"letter": "a", "text": "Lowest fees in the market"}, {"letter": "b", "text": "CopyTrading - automatically replicate successful investors' trades"}, {"letter": "c", "text": "Advanced charting tools"}, {"letter": "d", "text": "Cryptocurrency mining features"}]}, {"number": 4, "text": "Which platform is described as best for serious traders and professionals?", "options": [{"letter": "a", "text": "eToro"}, {"letter": "b", "text": "Robinhood"}, {"letter": "c", "text": "Interactive Brokers"}, {"letter": "d", "text": "Trading 212"}]}, {"number": 5, "text": "What should you do in a demo account that you wouldn't do with real money?", "options": [{"letter": "a", "text": "Only make safe, conservative trades"}, {"letter": "b", "text": "Avoid learning new features"}, {"letter": "c", "text": "Try risky moves and experiment to understand consequences"}, {"letter": "d", "text": "Focus only on making profits"}]}]'::jsonb,
'["b", "c", "b", "c", "c"]'::jsonb,
'Test your understanding of demo accounts and trading platforms');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (3, 'Day 3: Essential Trading Terms & Order Types', true, 
'[{"number": 1, "text": "What is the 'spread' in trading?", "options": [{"letter": "a", "text": "The difference between bid and ask price"}, {"letter": "b", "text": "The daily price range"}, {"letter": "c", "text": "The profit from a trade"}, {"letter": "d", "text": "The broker's commission"}]}, {"number": 2, "text": "Which order type guarantees execution but NOT price?", "options": [{"letter": "a", "text": "Limit order"}, {"letter": "b", "text": "Market order"}, {"letter": "c", "text": "Stop limit order"}, {"letter": "d", "text": "Stop loss order"}]}, {"number": 3, "text": "What does 'going short' mean?", "options": [{"letter": "a", "text": "Holding for a short time period"}, {"letter": "b", "text": "Buying small position sizes"}, {"letter": "c", "text": "Selling an asset expecting price to fall"}, {"letter": "d", "text": "Using less leverage"}]}, {"number": 4, "text": "With 10:1 leverage and \u00a31,000 capital, what position size can you control?", "options": [{"letter": "a", "text": "\u00a3100"}, {"letter": "b", "text": "\u00a31,000"}, {"letter": "c", "text": "\u00a310,000"}, {"letter": "d", "text": "\u00a3100,000"}]}, {"number": 5, "text": "What is the PRIMARY purpose of a stop loss order?", "options": [{"letter": "a", "text": "To guarantee profits"}, {"letter": "b", "text": "To limit potential losses"}, {"letter": "c", "text": "To increase leverage"}, {"letter": "d", "text": "To enter at better prices"}]}]'::jsonb,
'["a", "b", "c", "c", "b"]'::jsonb,
'Test your understanding of trading terms and order types');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (4, 'Day 4: Investment vs Trading - Understanding the Difference', false, 
'[{"number": 1, "text": "What is the main difference between investing and trading?", "options": [{"letter": "a", "text": "Investing requires more money than trading"}, {"letter": "b", "text": "Investing is long-term wealth building, trading is short-term profit seeking"}, {"letter": "c", "text": "There is no real difference"}, {"letter": "d", "text": "Trading is safer than investing"}]}, {"number": 2, "text": "What percentage of day traders are consistently profitable?", "options": [{"letter": "a", "text": "50-60%"}, {"letter": "b", "text": "30-40%"}, {"letter": "c", "text": "10-20%"}, {"letter": "d", "text": "70-80%"}]}, {"number": 3, "text": "Which analysis method is primarily used by long-term investors?", "options": [{"letter": "a", "text": "Technical analysis only"}, {"letter": "b", "text": "Fundamental analysis"}, {"letter": "c", "text": "Sentiment analysis"}, {"letter": "d", "text": "No analysis needed"}]}, {"number": 4, "text": "What is a core-satellite strategy?", "options": [{"letter": "a", "text": "Trading only technology stocks"}, {"letter": "b", "text": "80% long-term investments, 20% active trading"}, {"letter": "c", "text": "Only investing in index funds"}, {"letter": "d", "text": "Day trading with all your capital"}]}, {"number": 5, "text": "How are long-term investment gains typically taxed compared to short-term trading gains?", "options": [{"letter": "a", "text": "They are taxed the same"}, {"letter": "b", "text": "Long-term gains have lower tax rates"}, {"letter": "c", "text": "Short-term gains have lower tax rates"}, {"letter": "d", "text": "Neither is taxed"}]}]'::jsonb,
'["b", "c", "b", "b", "b"]'::jsonb,
'Test your understanding of investing vs trading');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (5, 'Day 5: Market Indexes', false, 
'[{"number": 1, "text": "What does a market index measure?", "options": [{"letter": "a", "text": "The performance of a section of the stock market"}, {"letter": "b", "text": "Only the largest company in a market"}, {"letter": "c", "text": "The total money in the stock market"}, {"letter": "d", "text": "Government economic policy"}]}, {"number": 2, "text": "How many companies are in the S&P 500 index?", "options": [{"letter": "a", "text": "30"}, {"letter": "b", "text": "100"}, {"letter": "c", "text": "500"}, {"letter": "d", "text": "1000"}]}, {"number": 3, "text": "Which index is price-weighted?", "options": [{"letter": "a", "text": "S&P 500"}, {"letter": "b", "text": "Dow Jones Industrial Average"}, {"letter": "c", "text": "FTSE 100"}, {"letter": "d", "text": "Nasdaq 100"}]}, {"number": 4, "text": "What is the main advantage of investing in an index fund?", "options": [{"letter": "a", "text": "Guaranteed profits"}, {"letter": "b", "text": "Automatic diversification across many companies"}, {"letter": "c", "text": "No risk involved"}, {"letter": "d", "text": "Higher returns than individual stocks always"}]}, {"number": 5, "text": "Which index is considered tech-heavy?", "options": [{"letter": "a", "text": "Dow Jones"}, {"letter": "b", "text": "FTSE 100"}, {"letter": "c", "text": "Nasdaq 100"}, {"letter": "d", "text": "DAX"}]}]'::jsonb,
'["a", "c", "b", "b", "c"]'::jsonb,
'Test your understanding of market indexes');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (6, 'Day 6: Stop Loss, Take Profit, Leverage & Margin', false, 
'[{"number": 1, "text": "What is the PRIMARY purpose of a stop loss order?", "options": [{"letter": "a", "text": "To guarantee profits on winning trades"}, {"letter": "b", "text": "To limit potential losses and protect capital"}, {"letter": "c", "text": "To automatically buy more when price drops"}, {"letter": "d", "text": "To increase your leverage ratio"}]}, {"number": 2, "text": "If you buy a stock at $100 with a stop loss at $95 and take profit at $115, what is your risk-reward ratio?", "options": [{"letter": "a", "text": "1:1"}, {"letter": "b", "text": "1:2"}, {"letter": "c", "text": "1:3"}, {"letter": "d", "text": "2:1"}]}, {"number": 3, "text": "With 10:1 leverage and \u00a31,000 capital, how much can you control?", "options": [{"letter": "a", "text": "\u00a31,000"}, {"letter": "b", "text": "\u00a35,000"}, {"letter": "c", "text": "\u00a310,000"}, {"letter": "d", "text": "\u00a3100,000"}]}, {"number": 4, "text": "What happens during a margin call?", "options": [{"letter": "a", "text": "Your broker gives you free money"}, {"letter": "b", "text": "Your leverage automatically increases"}, {"letter": "c", "text": "Your positions are closed if you can't add more funds"}, {"letter": "d", "text": "Nothing, it's just a warning"}]}, {"number": 5, "text": "According to the 1% rule, if you have a \u00a310,000 account, what's the maximum you should risk per trade?", "options": [{"letter": "a", "text": "\u00a310"}, {"letter": "b", "text": "\u00a3100"}, {"letter": "c", "text": "\u00a31,000"}, {"letter": "d", "text": "\u00a35,000"}]}]'::jsonb,
'["b", "c", "c", "c", "b"]'::jsonb,
'Test your understanding of risk management and leverage');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (7, 'Day 7: Reading Price Charts & Candlestick Basics', false, 
'[{"number": 1, "text": "What does a green/white candlestick body indicate?", "options": [{"letter": "a", "text": "Price closed higher than it opened (bullish)"}, {"letter": "b", "text": "Price closed lower than it opened (bearish)"}, {"letter": "c", "text": "Price stayed the same all day"}, {"letter": "d", "text": "The stock is ready to crash"}]}, {"number": 2, "text": "A Hammer candlestick is most reliable when it appears where?", "options": [{"letter": "a", "text": "At the bottom of a downtrend"}, {"letter": "b", "text": "At the top of an uptrend"}, {"letter": "c", "text": "In the middle of a sideways trend"}, {"letter": "d", "text": "Location doesn't matter"}]}, {"number": 3, "text": "What does a Doji candlestick indicate?", "options": [{"letter": "a", "text": "Strong bullish momentum"}, {"letter": "b", "text": "Strong bearish momentum"}, {"letter": "c", "text": "Indecision between buyers and sellers (open \u2248 close)"}, {"letter": "d", "text": "The market is closed"}]}, {"number": 4, "text": "A Bullish Engulfing pattern consists of:", "options": [{"letter": "a", "text": "One large green candle"}, {"letter": "b", "text": "Small red candle followed by large green candle that engulfs it"}, {"letter": "c", "text": "Three green candles in a row"}, {"letter": "d", "text": "A candle with no body"}]}, {"number": 5, "text": "What do long upper and lower wicks on a candlestick indicate?", "options": [{"letter": "a", "text": "Strong trend continuation"}, {"letter": "b", "text": "Price rejection and volatility at those levels"}, {"letter": "c", "text": "The stock will definitely go up"}, {"letter": "d", "text": "Low trading volume"}]}]'::jsonb,
'["a", "a", "c", "b", "b"]'::jsonb,
'Test your understanding of price charts and candlestick patterns');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (8, 'Day 8: Trend Lines & Chart Patterns', false, 
'[{"number": 1, "text": "What defines a valid uptrend?", "options": [{"letter": "a", "text": "Series of higher highs and higher lows"}, {"letter": "b", "text": "Series of lower highs and lower lows"}, {"letter": "c", "text": "Price moving sideways"}, {"letter": "d", "text": "Random price movements"}]}, {"number": 2, "text": "A bull flag pattern indicates what?", "options": [{"letter": "a", "text": "Trend reversal to the downside"}, {"letter": "b", "text": "Trend continuation to the upside"}, {"letter": "c", "text": "Market will move sideways"}, {"letter": "d", "text": "No predictable movement"}]}, {"number": 3, "text": "How many points minimum do you need to draw a valid trend line?", "options": [{"letter": "a", "text": "1 point"}, {"letter": "b", "text": "2 points"}, {"letter": "c", "text": "3 points"}, {"letter": "d", "text": "4 points"}]}, {"number": 4, "text": "What does a Head & Shoulders pattern signal?", "options": [{"letter": "a", "text": "Bullish continuation"}, {"letter": "b", "text": "Bearish reversal (uptrend ending)"}, {"letter": "c", "text": "Strong uptrend beginning"}, {"letter": "d", "text": "Sideways movement"}]}, {"number": 5, "text": "What is the most important confirmation for a valid pattern breakout?", "options": [{"letter": "a", "text": "Price color (green or red)"}, {"letter": "b", "text": "High volume (50%+ above average)"}, {"letter": "c", "text": "Time of day"}, {"letter": "d", "text": "The broker you use"}]}]'::jsonb,
'["a", "b", "b", "b", "b"]'::jsonb,
'Test your understanding of trend lines and chart patterns');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (9, 'Day 9: Moving Averages & Support/Resistance', false, 
'[{"number": 1, "text": "What does it mean when price is ABOVE the 200 SMA?", "options": [{"letter": "a", "text": "The long-term trend is bullish/uptrend"}, {"letter": "b", "text": "The long-term trend is bearish/downtrend"}, {"letter": "c", "text": "The stock will crash soon"}, {"letter": "d", "text": "Moving averages don't indicate trend"}]}, {"number": 2, "text": "What is the main difference between SMA and EMA?", "options": [{"letter": "a", "text": "SMA is always more accurate"}, {"letter": "b", "text": "EMA gives more weight to recent prices, reacts faster"}, {"letter": "c", "text": "They are exactly the same"}, {"letter": "d", "text": "EMA can only be used for day trading"}]}, {"number": 3, "text": "What happens when resistance is broken with high volume?", "options": [{"letter": "a", "text": "It becomes new support (role reversal)"}, {"letter": "b", "text": "Price will immediately crash"}, {"letter": "c", "text": "Nothing changes"}, {"letter": "d", "text": "Resistance gets stronger"}]}, {"number": 4, "text": "What makes a support level \"strong\"?", "options": [{"letter": "a", "text": "Price only touched it once"}, {"letter": "b", "text": "Multiple touches (3+), high volume, long history"}, {"letter": "c", "text": "It's a random price level"}, {"letter": "d", "text": "All support levels are equally strong"}]}, {"number": 5, "text": "What is a \"Power Combo\" setup in Day 9?", "options": [{"letter": "a", "text": "Using only one indicator"}, {"letter": "b", "text": "Triple confirmation: Trend (MA) + Level (S/R) + Action (pattern/volume)"}, {"letter": "c", "text": "Trading without stop loss"}, {"letter": "d", "text": "Using as many indicators as possible"}]}]'::jsonb,
'["a", "b", "a", "b", "b"]'::jsonb,
'Test your understanding of Moving Averages & Support/Resistance');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (10, 'Day 10: Chart Patterns', false, 
'[{"number": 1, "text": "What type of pattern is a Bull Flag, and when does it appear?", "options": [{"letter": "a", "text": "Continuation pattern that appears mid-uptrend"}, {"letter": "b", "text": "Reversal pattern at top of uptrend"}, {"letter": "c", "text": "Only works in downtrends"}, {"letter": "d", "text": "Reversal pattern at bottom of downtrend"}]}, {"number": 2, "text": "What does a Head & Shoulders pattern signal?", "options": [{"letter": "a", "text": "Trend continuation in same direction"}, {"letter": "b", "text": "Bearish reversal - uptrend is exhausted"}, {"letter": "c", "text": "Bullish reversal - downtrend ending"}, {"letter": "d", "text": "No predictable outcome"}]}, {"number": 3, "text": "How do you calculate the target for a Cup & Handle pattern?", "options": [{"letter": "a", "text": "Cup depth added to breakout point"}, {"letter": "b", "text": "Handle height added to breakout"}, {"letter": "c", "text": "No mathematical target"}, {"letter": "d", "text": "Always 10% above breakout"}]}, {"number": 4, "text": "What is the most important confirmation for a pattern breakout?", "options": [{"letter": "a", "text": "Time of day"}, {"letter": "b", "text": "High volume (50-100%+ above average)"}, {"letter": "c", "text": "Price color (green or red)"}, {"letter": "d", "text": "Pattern size"}]}, {"number": 5, "text": "What makes a pattern setup \"high probability\" according to Day 10?", "options": [{"letter": "a", "text": "Just identifying the pattern correctly"}, {"letter": "b", "text": "Using only one confirmation factor"}, {"letter": "c", "text": "5-Point Checklist: Pattern + Trend + MA + S/R + Volume/RR (4-5 checks passed)"}, {"letter": "d", "text": "Trading every pattern you see"}]}]'::jsonb,
'["a", "b", "a", "b", "c"]'::jsonb,
'Test your understanding of Chart Patterns');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (11, 'Day 11: Key Indicators - RSI, MACD, MAs & Economic Cycle', false, 
'[{"number": 1, "text": "What does RSI below 30 indicate?", "options": [{"letter": "a", "text": "Oversold condition - potential bounce coming (but check trend first!)"}, {"letter": "b", "text": "Guaranteed buy signal regardless of trend"}, {"letter": "c", "text": "Overbought condition - sell immediately"}, {"letter": "d", "text": "The indicator is broken"}]}, {"number": 2, "text": "What is the most reliable MACD signal?", "options": [{"letter": "a", "text": "Every time MACD crosses signal line"}, {"letter": "b", "text": "MACD bullish cross WITH RSI confirmation AND in uptrend"}, {"letter": "c", "text": "Histogram getting bigger"}, {"letter": "d", "text": "MACD is always 100% accurate"}]}, {"number": 3, "text": "What is \"bullish divergence\"?", "options": [{"letter": "a", "text": "Price makes lower low, but RSI/MACD makes higher low (momentum improving)"}, {"letter": "b", "text": "Price and indicators both rising together"}, {"letter": "c", "text": "Price makes higher high, indicators make lower high"}, {"letter": "d", "text": "When all indicators disagree"}]}, {"number": 4, "text": "How should indicators be interpreted during the Expansion phase of the economic cycle?", "options": [{"letter": "a", "text": "RSI dips to 30-40 are strong buy signals, MACD crosses work well, buy dips"}, {"letter": "b", "text": "Only short the market during expansion"}, {"letter": "c", "text": "Indicators don't work in expansion"}, {"letter": "d", "text": "Stay in cash during expansion"}]}, {"number": 5, "text": "What is the \"Triple Confirmation System\" for high-probability trades?", "options": [{"letter": "a", "text": "Using any three indicators randomly"}, {"letter": "b", "text": "MA trend + RSI signal + MACD confirmation + Entry near MA level (all aligned)"}, {"letter": "c", "text": "Only using RSI three times"}, {"letter": "d", "text": "Trading without any confirmation"}]}]'::jsonb,
'["a", "b", "a", "a", "b"]'::jsonb,
'Test your understanding of RSI, MACD, MAs & Economic Cycle');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (12, 'Day 12: Introduction to Fundamental Analysis', false, 
'[{"number": 1, "text": "What's the main difference between technical and fundamental analysis?", "options": [{"letter": "a", "text": "Technical analyzes charts/price, Fundamental analyzes company value/financials"}, {"letter": "b", "text": "They're the same thing"}, {"letter": "c", "text": "Technical is for stocks, fundamental is for forex"}, {"letter": "d", "text": "Technical is for beginners, fundamental for experts"}]}, {"number": 2, "text": "What is the Top-Down approach in fundamental analysis?", "options": [{"letter": "a", "text": "Start with economy \u2192 industry \u2192 company (broad to specific)"}, {"letter": "b", "text": "Start with company \u2192 industry \u2192 economy"}, {"letter": "c", "text": "Only analyze companies, ignore economy"}, {"letter": "d", "text": "Focus only on stock charts"}]}, {"number": 3, "text": "What are the THREE core financial statements?", "options": [{"letter": "a", "text": "Income Statement, Balance Sheet, Cash Flow Statement"}, {"letter": "b", "text": "P/E ratio, ROE, Debt ratio"}, {"letter": "c", "text": "Charts, Patterns, Indicators"}, {"letter": "d", "text": "Only need income statement"}]}, {"number": 4, "text": "Where can you find official company financial reports for FREE?", "options": [{"letter": "a", "text": "SEC EDGAR, Yahoo Finance, company investor relations websites"}, {"letter": "b", "text": "Only paid subscriptions have this data"}, {"letter": "c", "text": "Social media and chat forums"}, {"letter": "d", "text": "Companies keep this information secret"}]}, {"number": 5, "text": "Why should traders use BOTH technical AND fundamental analysis?", "options": [{"letter": "a", "text": "Technical shows WHEN to trade (timing), Fundamental shows WHAT to trade (selection)"}, {"letter": "b", "text": "You only need one, not both"}, {"letter": "c", "text": "To confuse yourself with more data"}, {"letter": "d", "text": "Professional traders only use charts"}]}]'::jsonb,
'["a", "a", "a", "a", "a"]'::jsonb,
'Mission: Familiarize yourself with fundamental analysis tools and find a company to analyze!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (13, 'Day 13: Stock Research & Key Financial Ratios', false, 
'[{"number": 1, "text": "Company has P/E of 10 while industry average is 20. What does this mean?", "options": [{"letter": "a", "text": "Stock is potentially undervalued - investors pay less per dollar of earnings"}, {"letter": "b", "text": "Stock is overvalued - too expensive"}, {"letter": "c", "text": "Company is losing money"}, {"letter": "d", "text": "P/E ratio doesn't matter"}]}, {"number": 2, "text": "What is the BEST indicator of how efficiently a company uses shareholders' money?", "options": [{"letter": "a", "text": "ROE (Return on Equity) - shows profit generated per dollar of equity"}, {"letter": "b", "text": "Stock price"}, {"letter": "c", "text": "Revenue"}, {"letter": "d", "text": "Market cap"}]}, {"number": 3, "text": "Company has Debt-to-Equity of 3.0. What's the risk level?", "options": [{"letter": "a", "text": "HIGH RISK - Company heavily leveraged with too much debt"}, {"letter": "b", "text": "Low risk - very safe"}, {"letter": "c", "text": "Perfect balance"}, {"letter": "d", "text": "Debt doesn't matter"}]}, {"number": 4, "text": "Where can you find official company financial statements?", "options": [{"letter": "a", "text": "SEC EDGAR (10-K annual, 10-Q quarterly reports) - official government filings"}, {"letter": "b", "text": "Only from paid subscriptions"}, {"letter": "c", "text": "Social media tips"}, {"letter": "d", "text": "Company doesn't share this info"}]}, {"number": 5, "text": "What's Warren Buffett's minimum ROE target?", "options": [{"letter": "a", "text": "20% - He only invests in highly efficient companies"}, {"letter": "b", "text": "5%"}, {"letter": "c", "text": "50%"}, {"letter": "d", "text": "Buffett doesn't use ROE"}]}]'::jsonb,
'["a", "a", "a", "a", "a"]'::jsonb,
'Mission: Research and analyze 3 different stocks using financial ratios!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (14, 'Day 14: Simulated Trading Exercise', false, 
'[{"number": 1, "text": "What is the MINIMUM number of confirmations you should have before taking a trade?", "options": [{"letter": "a", "text": "1 confirmation (just a pattern or indicator)"}, {"letter": "b", "text": "2 confirmations (pattern + indicator)"}, {"letter": "c", "text": "4+ confirmations (trend + S/R + pattern + indicators + fundamentals + risk plan)"}, {"letter": "d", "text": "No confirmations needed, just trade on gut feeling"}]}, {"number": 2, "text": "You find a trade setup with 3 confirmations. Entry $100, Stop $95, Target $110. Risk/Reward is 1:2. Should you take this trade?", "options": [{"letter": "a", "text": "Yes, R:R is 1:2 and you have confirmations"}, {"letter": "b", "text": "No, wait for 4+ confirmations for highest probability"}, {"letter": "c", "text": "Yes, but only if FOMO is driving the decision"}, {"letter": "d", "text": "Yes, ignore confirmations and just trade"}]}, {"number": 3, "text": "Which is MORE important: winning percentage or following your process?", "options": [{"letter": "a", "text": "Winning percentage - you must win 80%+ to be profitable"}, {"letter": "b", "text": "Following your process - even with 40-50% win rate, you profit with good risk management"}, {"letter": "c", "text": "Both are equally important"}, {"letter": "d", "text": "Neither matters, just make money somehow"}]}, {"number": 4, "text": "You're on Trade #4 and the first 3 trades lost. What should you do?", "options": [{"letter": "a", "text": "Double your position size to make back losses faster (revenge trading)"}, {"letter": "b", "text": "Stop trading for today - you're clearly doing something wrong"}, {"letter": "c", "text": "Continue with same risk (1-2%), follow your process, review what went wrong later"}, {"letter": "d", "text": "Remove stop losses to give trades more room"}]}, {"number": 5, "text": "What's the PRIMARY goal of today's simulated trading exercise?", "options": [{"letter": "a", "text": "Win all 5 trades and make maximum profit"}, {"letter": "b", "text": "Prove you can identify high-quality setups and follow a complete trading process"}, {"letter": "c", "text": "Take as many trades as possible for practice"}, {"letter": "d", "text": "Test different strategies randomly to see what works"}]}]'::jsonb,
'["c", "b", "b", "c", "b"]'::jsonb,
'Mission: Execute 5 simulated trades using everything from Phase 2!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (15, 'Day 15: Risk Management & Position Sizing', false, 
'[{"number": 1, "text": "What is the MAXIMUM amount professional traders risk per trade?", "options": [{"letter": "a", "text": "1-2% of account"}, {"letter": "b", "text": "5-10% of account"}, {"letter": "c", "text": "10-20% of account"}, {"letter": "d", "text": "As much as feels right"}]}, {"number": 2, "text": "If you lose 50% of your account, what % gain do you need to break even?", "options": [{"letter": "a", "text": "50%"}, {"letter": "b", "text": "75%"}, {"letter": "c", "text": "100%"}, {"letter": "d", "text": "150%"}]}, {"number": 3, "text": "What is the MINIMUM acceptable risk-reward ratio?", "options": [{"letter": "a", "text": "1:1 (risk $1 to make $1)"}, {"letter": "b", "text": "1:2 (risk $1 to make $2)"}, {"letter": "c", "text": "1:5 (risk $1 to make $5)"}, {"letter": "d", "text": "No minimum - any ratio works"}]}, {"number": 4, "text": "Your trade is going against you. What should you do with your stop loss?", "options": [{"letter": "a", "text": "Move it further away to give trade more room"}, {"letter": "b", "text": "Remove it completely and wait for reversal"}, {"letter": "c", "text": "Keep it exactly where it is - NEVER move against you"}, {"letter": "d", "text": "Double your position to average down"}]}, {"number": 5, "text": "You have $10,000 account, risk 1%, entry $100, stop $98. How many shares?", "options": [{"letter": "a", "text": "10 shares"}, {"letter": "b", "text": "50 shares"}, {"letter": "c", "text": "100 shares"}, {"letter": "d", "text": "As many as you can afford"}]}]'::jsonb,
'["a", "c", "b", "c", "b"]'::jsonb,
'Mission: Calculate position sizes for 5 different trades and prove you understand risk management!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (16, 'Day 16: Moving Averages & Crossovers Strategy', false, 
'[{"number": 1, "text": "What is a Golden Cross?", "options": [{"letter": "a", "text": "50 SMA crosses above 200 SMA (super bullish signal)"}, {"letter": "b", "text": "20 SMA crosses above 50 SMA"}, {"letter": "c", "text": "Price crosses above any moving average"}, {"letter": "d", "text": "50 SMA crosses below 200 SMA"}]}, {"number": 2, "text": "Which filter is MOST effective at reducing false crossover signals?", "options": [{"letter": "a", "text": "Only trade crossovers in direction of 200 SMA trend"}, {"letter": "b", "text": "Trade every crossover regardless of trend"}, {"letter": "c", "text": "Only trade on Mondays"}, {"letter": "d", "text": "Ignore volume completely"}]}, {"number": 3, "text": "Best MA crossover for swing trading (holding days to weeks)?", "options": [{"letter": "a", "text": "5/20 EMA (too fast, many false signals)"}, {"letter": "b", "text": "20/50 SMA (balanced, reliable)"}, {"letter": "c", "text": "200 SMA only (no crossover)"}, {"letter": "d", "text": "Don't use MAs for swing trading"}]}, {"number": 4, "text": "You see a bullish crossover but price is 15% above the fast MA. What should you do?", "options": [{"letter": "a", "text": "Buy immediately - crossover is always right"}, {"letter": "b", "text": "Skip trade - price overextended, high risk of pullback"}, {"letter": "c", "text": "Short sell instead"}, {"letter": "d", "text": "Use maximum leverage"}]}, {"number": 5, "text": "Where should stop loss be placed for a long trade entered on bullish 20/50 crossover?", "options": [{"letter": "a", "text": "Below the 50 SMA (slow MA)"}, {"letter": "b", "text": "Above the 20 SMA (fast MA)"}, {"letter": "c", "text": "No stop loss needed"}, {"letter": "d", "text": "At random price 20% below"}]}]'::jsonb,
'["a", "a", "b", "b", "a"]'::jsonb,
'Mission: Build and backtest your own MA crossover system!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (17, 'Day 17: Fibonacci Retracements & Advanced Chart Analysis', false, 
'[{"number": 1, "text": "What is the MOST important Fibonacci level (Golden Ratio)?", "options": [{"letter": "a", "text": "61.8% - highest probability reversal level"}, {"letter": "b", "text": "50% - psychological level"}, {"letter": "c", "text": "23.6% - first retracement"}, {"letter": "d", "text": "100% - starting point"}]}, {"number": 2, "text": "How do you draw Fibonacci for an UPTREND?", "options": [{"letter": "a", "text": "From swing LOW to swing HIGH (bottom to top)"}, {"letter": "b", "text": "From swing HIGH to swing LOW (top to bottom)"}, {"letter": "c", "text": "From current price to previous price"}, {"letter": "d", "text": "From any point to any point"}]}, {"number": 3, "text": "What makes a \"Triple Confluence\" setup so powerful?", "options": [{"letter": "a", "text": "3+ factors align at same price (Fib + S/R + MA) = 75-85% win rate"}, {"letter": "b", "text": "You take 3 trades at the same time"}, {"letter": "c", "text": "Three traders agree on the setup"}, {"letter": "d", "text": "Trade three different assets"}]}, {"number": 4, "text": "Price breaks below 78.6% Fibonacci level. What does this mean?", "options": [{"letter": "a", "text": "The trend is likely over - don't trade this retracement"}, {"letter": "b", "text": "Best buying opportunity - go all in!"}, {"letter": "c", "text": "It's just a deeper retracement, still bullish"}, {"letter": "d", "text": "Double your position size"}]}, {"number": 5, "text": "Best confirmation signal to enter at a Fib level?", "options": [{"letter": "a", "text": "Enter immediately when price touches Fib level"}, {"letter": "b", "text": "Wait for reversal candle pattern + volume spike + RSI confirmation"}, {"letter": "c", "text": "Use gut feeling"}, {"letter": "d", "text": "No confirmation needed with Fibonacci"}]}]'::jsonb,
'["a", "a", "a", "a", "b"]'::jsonb,
'Mission: Find and analyze Fibonacci retracements on real charts!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (18, 'Day 18: Backtesting Strategies & Chart Review', false, 
'[{"number": 1, "text": "What is the MINIMUM number of trades needed for statistical significance in backtesting?", "options": [{"letter": "a", "text": "100+ trades (statistically significant)"}, {"letter": "b", "text": "20 trades (enough)"}, {"letter": "c", "text": "5 trades (good sample)"}, {"letter": "d", "text": "No minimum needed"}]}, {"number": 2, "text": "A strategy has 40% win rate but 1:3 risk-reward. Is it profitable?", "options": [{"letter": "a", "text": "YES - Makes $20 per 10 trades: (4 wins \u00d7 $30) - (6 losses \u00d7 $10)"}, {"letter": "b", "text": "NO - Win rate too low"}, {"letter": "c", "text": "Need 60%+ win rate to be profitable"}, {"letter": "d", "text": "Cannot determine without more data"}]}, {"number": 3, "text": "What is \"curve fitting\" in backtesting?", "options": [{"letter": "a", "text": "Over-optimizing strategy to work ONLY on past data (won't work forward)"}, {"letter": "b", "text": "Drawing trend lines on charts"}, {"letter": "c", "text": "Testing strategy properly"}, {"letter": "d", "text": "Using curved monitors for trading"}]}, {"number": 4, "text": "What is a good Profit Factor?", "options": [{"letter": "a", "text": "Above 1.5 (winning $1.50+ for every $1 lost)"}, {"letter": "b", "text": "Below 1.0 (losing system)"}, {"letter": "c", "text": "Exactly 1.0 (breakeven)"}, {"letter": "d", "text": "Doesn't matter"}]}, {"number": 5, "text": "After backtesting shows a strategy works, what should you do BEFORE going live?", "options": [{"letter": "a", "text": "Paper trade for 30 days to confirm results match backtest"}, {"letter": "b", "text": "Go live immediately with full account"}, {"letter": "c", "text": "Skip forward testing - backtest is enough"}, {"letter": "d", "text": "Tell everyone on social media"}]}]'::jsonb,
'["a", "a", "a", "a", "a"]'::jsonb,
'Mission: Backtest one of your strategies on historical data!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (19, 'Day 19: Creating a Trading Plan & ROI', false, 
'[{"number": 1, "text": "What percentage of traders have a written trading plan?", "options": [{"letter": "a", "text": "Only 10% have written plans"}, {"letter": "b", "text": "About 50% have written plans"}, {"letter": "c", "text": "Most traders (80%) have written plans"}, {"letter": "d", "text": "Plans are not necessary"}]}, {"number": 2, "text": "What is a realistic annual ROI for an intermediate trader?", "options": [{"letter": "a", "text": "15-30% annual ROI"}, {"letter": "b", "text": "200% monthly ROI"}, {"letter": "c", "text": "500% annual ROI"}, {"letter": "d", "text": "5% annual ROI"}]}, {"number": 3, "text": "Which of these is the BEST entry rule for a trading plan?", "options": [{"letter": "a", "text": "\"Buy when price above 50 MA + RSI below 40 + MACD bullish crossover\""}, {"letter": "b", "text": "\"Buy when it looks good\""}, {"letter": "c", "text": "\"Buy when I feel confident\""}, {"letter": "d", "text": "\"Buy when the news is positive\""}]}, {"number": 4, "text": "If you start with $10,000 and achieve 2% monthly returns (compounded), what will you have after 1 year?", "options": [{"letter": "a", "text": "~$12,682 (compounded growth)"}, {"letter": "b", "text": "$12,400 (simple 24%)"}, {"letter": "c", "text": "$11,000"}, {"letter": "d", "text": "$10,240"}]}, {"number": 5, "text": "What should you do after 3 losses in a row according to best practices?", "options": [{"letter": "a", "text": "Stop trading for the day (circuit breaker)"}, {"letter": "b", "text": "Double your position size to recover losses"}, {"letter": "c", "text": "Switch to a different strategy immediately"}, {"letter": "d", "text": "Keep trading - losses are normal"}]}]'::jsonb,
'["a", "a", "a", "a", "a"]'::jsonb,
'Mission: Create YOUR complete trading plan!');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (20, 'Day 20: Trading Psychology â€“ Emotional Mastery', false, 
'[{"number": 1, "text": "1. What percentage of trading success is attributed to psychology vs technical knowledge?", "options": [{"letter": "a", "text": "80% psychology, 20% technical"}, {"letter": "b", "text": "50% psychology, 50% technical"}, {"letter": "c", "text": "20% psychology, 80% technical"}, {"letter": "d", "text": "100% technical knowledge"}]}, {"number": 2, "text": "2. After losing 2 trades in a row, what should you do?", "options": [{"letter": "a", "text": "Double position size to recover losses"}, {"letter": "b", "text": "Stop trading for the day"}, {"letter": "c", "text": "Switch to a different strategy"}, {"letter": "d", "text": "Trade more to make it back"}]}, {"number": 3, "text": "3. Which emotional state is BEST for trading?", "options": [{"letter": "a", "text": "Euphoric and excited"}, {"letter": "b", "text": "Angry and determined"}, {"letter": "c", "text": "Neutral to confident"}, {"letter": "d", "text": "Fearful and cautious"}]}, {"number": 4, "text": "4. What's more important: Financial capital or Psychological capital?", "options": [{"letter": "a", "text": "Financial capital only matters"}, {"letter": "b", "text": "Psychological capital is more important"}, {"letter": "c", "text": "Neither matters if you have a good strategy"}, {"letter": "d", "text": "Only financial capital can be measured"}]}, {"number": 5, "text": "5. When you miss a big move in a stock, what should you do?", "options": [{"letter": "a", "text": "Chase it immediately before it goes higher"}, {"letter": "b", "text": "Wait for the next setup opportunity"}, {"letter": "c", "text": "Increase position size on the next trade"}, {"letter": "d", "text": "Get angry at yourself for missing it"}]}]'::jsonb,
'["a", "b", "c", "b", "b"]'::jsonb,
'Tomorrow: Developing & Testing Your Strategy');

