INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (28, 'Day 28: Global Economic Indicators', true, 
'[{"number": 1, "text": "1. Which indicator is released on the first Friday of each month?", "options": [{"letter": "a", "text": "GDP"}, {"letter": "b", "text": "CPI"}, {"letter": "c", "text": "NFP (Non-Farm Payrolls)"}, {"letter": "d", "text": "Interest Rates"}]}, {"number": 2, "text": "2. What happens to currency when interest rates increase?", "options": [{"letter": "a", "text": "Currency strengthens (goes up)"}, {"letter": "b", "text": "Currency weakens (goes down)"}, {"letter": "c", "text": "No effect"}, {"letter": "d", "text": "Random movement"}]}, {"number": 3, "text": "3. What is the central bank's typical inflation target?", "options": [{"letter": "a", "text": "0%"}, {"letter": "b", "text": "2%"}, {"letter": "c", "text": "5%"}, {"letter": "d", "text": "10%"}]}, {"number": 4, "text": "4. How many quarters of negative GDP indicate a recession?", "options": [{"letter": "a", "text": "1 quarter"}, {"letter": "b", "text": "2 consecutive quarters"}, {"letter": "c", "text": "3 quarters"}, {"letter": "d", "text": "4 quarters"}]}, {"number": 5, "text": "5. What should you do before high-impact news?", "options": [{"letter": "a", "text": "Increase position size"}, {"letter": "b", "text": "Reduce or close positions"}, {"letter": "c", "text": "Add more leverage"}, {"letter": "d", "text": "Ignore the news"}]}]'::jsonb,
'["c", "a", "b", "b", "b"]'::jsonb,
'Day 29: SMART Goals Setting & Review');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (29, 'Day 29: SMART Goals Setting & Review', false, 
'[{"number": 1, "text": "1. What does the 'M' in SMART goals stand for?", "options": [{"letter": "a", "text": "Manageable"}, {"letter": "b", "text": "Measurable"}, {"letter": "c", "text": "Meaningful"}, {"letter": "d", "text": "Motivated"}]}, {"number": 2, "text": "2. What's a realistic monthly return goal for new traders?", "options": [{"letter": "a", "text": "50-100%"}, {"letter": "b", "text": "20-30%"}, {"letter": "c", "text": "3-5%"}, {"letter": "d", "text": "0%"}]}, {"number": 3, "text": "3. How often should you review your trading goals?", "options": [{"letter": "a", "text": "Weekly review, monthly adjustment"}, {"letter": "b", "text": "Once a year"}, {"letter": "c", "text": "Never change them"}, {"letter": "d", "text": "Daily changes"}]}, {"number": 4, "text": "4. What increases goal achievement rate to 95%?", "options": [{"letter": "a", "text": "Thinking about goals"}, {"letter": "b", "text": "Writing goals down"}, {"letter": "c", "text": "Regular accountability"}, {"letter": "d", "text": "Keeping goals secret"}]}, {"number": 5, "text": "5. How long minimum should you stay on demo?", "options": [{"letter": "a", "text": "1 month"}, {"letter": "b", "text": "3 months"}, {"letter": "c", "text": "6 months minimum"}, {"letter": "d", "text": "No minimum"}]}]'::jsonb,
'["b", "c", "a", "c", "c"]'::jsonb,
'Day 30: Final Assessment & Market Warrior Certification');

INSERT INTO course_content (day_number, title, has_video, quiz_questions, quiz_answers, task_instructions)
VALUES (30, 'Day 30: Final Assessment & Graduation', false, 
'[{"number": 1, "text": "What is the PRIMARY difference between investing and trading?", "options": [{"letter": "a", "text": "Investing is for beginners, trading is for experts"}, {"letter": "b", "text": "Investing is long-term (years), trading is short-term (days/weeks)"}, {"letter": "c", "text": "Investing uses stocks, trading uses crypto"}, {"letter": "d", "text": "There is no difference"}]}, {"number": 2, "text": "What is the recommended maximum risk per trade for beginners?", "options": [{"letter": "a", "text": "10%"}, {"letter": "b", "text": "5%"}, {"letter": "c", "text": "1-2%"}, {"letter": "d", "text": "Risk doesn't matter if you have stop losses"}]}, {"number": 3, "text": "When price is above the 200-day moving average, the trend is considered:", "options": [{"letter": "a", "text": "Bearish"}, {"letter": "b", "text": "Bullish"}, {"letter": "c", "text": "Neutral"}, {"letter": "d", "text": "Oversold"}]}, {"number": 4, "text": "What does RSI above 70 typically indicate?", "options": [{"letter": "a", "text": "Stock is undervalued"}, {"letter": "b", "text": "Stock is overbought (potential reversal down)"}, {"letter": "c", "text": "Strong buy signal"}, {"letter": "d", "text": "No useful information"}]}, {"number": 5, "text": "A bullish MACD crossover occurs when:", "options": [{"letter": "a", "text": "MACD line crosses above signal line"}, {"letter": "b", "text": "MACD line crosses below signal line"}, {"letter": "c", "text": "Both lines cross zero"}, {"letter": "d", "text": "Histogram turns negative"}]}, {"number": 6, "text": "What is the MOST important rule in trading psychology?", "options": [{"letter": "a", "text": "Never lose money"}, {"letter": "b", "text": "Follow your emotions"}, {"letter": "c", "text": "Stick to your trading plan no matter what"}, {"letter": "d", "text": "Trade as frequently as possible"}]}, {"number": 7, "text": "In fundamental analysis, what does P/E ratio measure?", "options": [{"letter": "a", "text": "Profit to expenses"}, {"letter": "b", "text": "Price relative to earnings"}, {"letter": "c", "text": "Performance to expectations"}, {"letter": "d", "text": "Product to economy"}]}, {"number": 8, "text": "What should you do BEFORE major economic announcements (NFP, CPI)?", "options": [{"letter": "a", "text": "Open new large positions"}, {"letter": "b", "text": "Reduce position sizes or close positions"}, {"letter": "c", "text": "Remove all stop losses"}, {"letter": "d", "text": "Nothing - news doesn't affect markets"}]}, {"number": 9, "text": "A support level is best described as:", "options": [{"letter": "a", "text": "The highest price a stock can reach"}, {"letter": "b", "text": "A price level where buying pressure prevents further decline"}, {"letter": "c", "text": "The moving average price"}, {"letter": "d", "text": "A chart pattern"}]}, {"number": 10, "text": "What is the MINIMUM recommended demo trading period before going live?", "options": [{"letter": "a", "text": "1 week"}, {"letter": "b", "text": "1 month"}, {"letter": "c", "text": "3 months"}, {"letter": "d", "text": "6 months"}]}]'::jsonb,
'["b", "c", "b", "b", "a", "c", "b", "b", "b", "d"]'::jsonb,
'Test your complete Market Warrior knowledge!');

